import {
  DELETE_CELL,
  INSERT_CELL_BEFORE,
  MOVE_CELL,
  UPDATE_CELL,
} from "../action-types"
import {
  DeleteCellAction,
  Direction,
  InsertCellBeforeAction,
  MoveCellAction,
  UpdateCellAction,
} from "../actions"
import { CellTypes } from "../cell"

export const updateCell = (id: string, content: string): UpdateCellAction => {
  return {
    type: UPDATE_CELL,
    payload: {
      id,
      content,
    },
  }
}

export const deleteCell = (id: string): DeleteCellAction => {
  return {
    type: DELETE_CELL,
    payload: id,
  }
}

export const moveCell = (id: string, direction: Direction): MoveCellAction => {
  return {
    type: MOVE_CELL,
    payload: {
      id,
      direction,
    },
  }
}

export const insertCellBefore = (
  id: string,
  cellType: CellTypes
): InsertCellBeforeAction => {
  return {
    type: INSERT_CELL_BEFORE,
    payload: {
      id,
      type: cellType,
    },
  }
}
